COMMIT;

START TRANSACTION;

ALTER TABLE "Server" ADD "OwnerID" integer NOT NULL DEFAULT 1;

CREATE INDEX "IX_Server_OwnerID" ON "Server" ("OwnerID");

ALTER TABLE "Server" ADD CONSTRAINT "FK_Server_User_OwnerID" FOREIGN KEY ("OwnerID") REFERENCES "User" ("Id") ON DELETE CASCADE;

